// uses resovoir sampling
export const chooseKRandomElements = (arr, k) => {
    const reservoir = [];
    // put the first num into reservoir
    for (let i = 0; i < k; i++) {
        reservoir.push(arr[i]);
    }
    for (let i = k; i < arr.length; i++) {
        const j = Math.round(Math.random() * i);
        if (j < k) {
            reservoir[j] = arr[i];
        }
    }
    return reservoir;
};
